<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Dasbor extends MX_Controller {	public function __construct()	{		parent::__construct();		$this->page->template('app_tpl');	}	public function index()	{		if($this->session->userdata('pengguna') == '') {			redirect(base_url('login'));		}		else if(($this->session->userdata('user'))) {			redirect(base_url('apps'));		}				$this->page->view('dasbor_index');	}}/* End of file dasbor.php *//* Location: ./application/controllers/dasbor.php */