<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Login extends MX_Controller {	public function __construct()	{		parent::__construct();		// $this->page->template('landing_tpl');		$this->page->template('login_tpl');	}	public function index()	{		if($this->session->userdata('pengguna')) {			redirect(base_url('dasbor'));		}		$this->load->view("landing/landing_login", array (			'login_url' => site_url('/auth/auth/landing_login'),		));	}	public function admin()	{		if($this->session->userdata('pengguna')) {			redirect(base_url('dasbor'));		}		$this->page->view(NULL, array (			'login_url' => site_url('/auth/auth/login'),			'signup_url' => site_url('/auth/auth/signup'),			'resetpwd_url' => site_url('/auth/auth/resetpwd'),		));	}}/* End of file login.php *//* Location: ./application/controllers/login.php */